define(['jquery', 'core/modal_factory', 'core/templates'], function($, ModalFactory, Templates) {
  return {
      init: function() {
          $('#open-history').on('click', function(e) {
              e.preventDefault(); // Предотвращаем действие по умолчанию

              // Загружаем содержимое шаблона hello_world.mustache
              Templates.render('block_quadratic_calculator/history', {})
              .then(function(html, js) {
                  // Создаем и отображаем модальное окно с загруженным содержимым
                  return ModalFactory.create({
                      type: ModalFactory.types.ALERT,
                      title: 'Modal title',
                      body: html
                  });
              })
              .then(function(modal) {
                  modal.show();
                  // Запускаем дополнительный JavaScript, если он есть
                  Templates.runTemplateJS(js);
              });
          });
      }
  };
});